mapper.select:
SELECT 
	CONCAT('[',GROUP_CONCAT(tp1),']') tp1,CONCAT('[',GROUP_CONCAT(tp2),']') tp2,CONCAT('[',GROUP_CONCAT(tp3),']') tp3,CONCAT('[',GROUP_CONCAT(tp4),']') tp4,
	CONCAT('[',GROUP_CONCAT(tp5),']') tp5,CONCAT('[',GROUP_CONCAT(tp6),']') tp6,CONCAT('[',GROUP_CONCAT(tp7),']') tp7,CONCAT('[',GROUP_CONCAT(tp8),']') tp8,
	CONCAT('[',GROUP_CONCAT(tp9),']') tp9,CONCAT('[',GROUP_CONCAT(vc),']') vc,CONCAT('[',GROUP_CONCAT(va),']') va,CONCAT('[',GROUP_CONCAT(vb),']') vb,
	CONCAT('[',GROUP_CONCAT(ia),']') ia,CONCAT('[',GROUP_CONCAT(ib),']') ib,CONCAT('[',GROUP_CONCAT(ic),']') ic,CONCAT('[',GROUP_CONCAT(ac_time1),']') ac_time
	from
	(select  *,CONCAT("'",ac_time,"'") ac_time1
	from ${tableName}

	node_id = ${nodeId}
	and gateway_id = ${gatewayId}

		AND ac_time >= ${startTime}
		AND ac_time <= ${endTime}


	order by ac_time) a


select  *,CONCAT("'",ac_time,"'") ac_time1
from ${tableName}

node_id = ${ibusTpvi.nodeId}
and gateway_id = ${ibusTpvi.gatewayId}

	AND ac_time >= ${startTime}
	AND ac_time <= ${endTime}


order by ac_time desc

	limit ${pageBean.start},${pageBean.pageSize}


select count(*) from (select  *,CONCAT("'",ac_time,"'") ac_time1
from ${tableName}

node_id = ${ibusTpvi.nodeId}
and gateway_id = ${ibusTpvi.gatewayId}

	AND ac_time >= ${startTime}
	AND ac_time <= ${endTime}


order by ac_time) a

SELECT CONCAT('[',GROUP_CONCAT(tp1),']') tp1,CONCAT('[',GROUP_CONCAT(tp2),']') tp2,CONCAT('[',GROUP_CONCAT(tp3),']') tp3,CONCAT('[',GROUP_CONCAT(tp4),']') tp4, CONCAT('[',GROUP_CONCAT(tp5),']') tp5,CONCAT('[',GROUP_CONCAT(tp6),']') tp6,CONCAT('[',GROUP_CONCAT(tp7),']') tp7,CONCAT('[',GROUP_CONCAT(tp8),']') tp8, CONCAT('[',GROUP_CONCAT(tp9),']') tp9,CONCAT('[',GROUP_CONCAT(vc),']') vc,CONCAT('[',GROUP_CONCAT(va),']') va,CONCAT('[',GROUP_CONCAT(vb),']') vb, CONCAT('[',GROUP_CONCAT(ia),']') ia,CONCAT('[',GROUP_CONCAT(ib),']') ib,CONCAT('[',GROUP_CONCAT(ic),']') ic,CONCAT('[',GROUP_CONCAT(ac_time1),']') ac_time FROM (SELECT *,CONCAT("'",ac_time,"'") ac_time1 FROM ibus_tpvi
node_id = #{nodeId,jdbcType=INTEGER} and gateway_id = #{gatewayId,jdbcType=INTEGER}
ORDER BY ac_time) a

		select a.node_id id,a.tp1 tp1,a.tp2 ,a.tp3,a.tp4,a.tp5,a.tp6,a.tp7,a.tp8,a.tp9,a.va,a.vb,a.vc,a.ia,a.ib,a.ic,MAX(a.ac_time) acTime
FROM ibus_tpvi a
WHERE node_id=#{nodeId}
and gateway_id=#{gatewayId}
